export default class RequireLoader {
  protected requirePaths: {
    path: string;
    requireContext;
  }[] = [];

  public requirePath(path: string, requireContext): RequireLoader {
    this.requirePaths.push({
      path,
      requireContext,
    });

    return this;
  }

  public getRequirePaths() {
    return this.requirePaths;
  }
}
