<template>
  <line-content
    :title="title"
    :styleClass="styleClass"
    :explanation="explanation"
    :explanationLinkTo="explanationLinkTo"
    :alert="alert"
  >
    <template v-for="(checkbox, key3) in inputCheckboxs" :key="key3">
      <label
        class="checkbox"
        data-toggle="tooltip"
        :title="checkbox.tooltip"
      >
        <input
          :id="`${inputName}${key3}`"
          type="checkbox"
          :name="inputName + '[]'"
          :value="checkbox.value"
          :checked="
            (value !== undefined &&
              ((Array.isArray(value) &&
                value
                  .map((v) => v.toString())
                  .includes(checkbox.value.toString())) ||
                (!Array.isArray(value) &&
                  value.toString() == checkbox.value.toString()))) ||
            checkbox.checked
          "
          :disabled="disabled == true || checkbox.disabled"
        />
        {{ checkbox.text }}
      </label>
    </template>
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/edit-control-center/line-content/base/line-content.vue';

export default {
  components: {
    LineContent,
  },
  props: [
    'title',
    'styleClass',
    'value',
    'inputName',
    'inputCheckboxs',
    'inputWidth',
    'inputMaxlength',
    'afterInputText',
    'explanation',
    'explanationLinkTo',
    'disabled',
    'alert',
  ],
  data() {
    return {};
  },
  mounted() {},
};
</script>
