<template>
  <line-content
    :title="title"
    :styleClass="styleClass"
    :explanation="explanation"
    :explanationLinkTo="explanationLinkTo"
    :alert="alert"
  >
    <DatePicker
      :id="inputName"
      :inputName="inputName"
      :inputWidth="inputWidthData"
      :modelValue="value"
      :type="'date'"
    />
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/edit-control-center/line-content/base/line-content.vue';
import DatePicker from '@fanswoo/core/vue/plugins/date-picker/date-picker.vue';

export default {
  components: {
    LineContent,
    DatePicker,
  },
  props: [
    'title',
    'styleClass',
    'value',
    'inputName',
    'type',
    'inputWidth',
    'explanation',
    'explanationLinkTo',
    'alert',
  ],
  computed: {
    inputWidthData() {
      if (this.inputWidth) {
        return this.inputWidth;
      }
      return null;
    },
  },
};
</script>
