<template>
  <div>
    <input
      v-for="(value, key) in values"
      :key="key"
      type="hidden"
      :name="inputNameReal"
      :value="value"
    />
  </div>
</template>

<script>
export default {
  props: ['inputName', 'value'],
  data() {
    return {
      values: [],
      inputNameReal: '',
    };
  },
  mounted() {
    if (Array.isArray(this.value)) {
      this.values = this.value;
      const brackets = '[]';
      this.inputNameReal = this.inputName + brackets;
    } else {
      this.values.push(this.value);
      this.inputNameReal = this.inputName;
    }
  },
};
</script>
