<template>
  <line-content :title="title">
    <b-calendar
      :value="value"
      v-bind="labels['zh-TW'] || {}"
      :locale="'zh-TW'"
      :startWeekday="1"
      :hide-header="true"
      :show-decade-nav="false"
      :block="true"
      @selected="onChange($event)"
    ></b-calendar>
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/list-control-center/search-box/base/line-content.vue';

export default {
  props: ['inputPlaceholder', 'inputName', 'title', 'value'],
  components: {
    LineContent,
  },
  data: () => {
    return {
      labels: {
        'zh-TW': {
          weekdayHeaderFormat: 'narrow',
          labelPrevDecade: '過去十年',
          labelPrevYear: '去年',
          labelPrevMonth: '上個月',
          labelCurrentMonth: '目前月份',
          labelNextMonth: '下個月',
          labelNextYear: '明年',
          labelNextDecade: '下個十年',
          labelToday: '今天',
          labelSelected: '選擇日期',
          labelNoDateSelected: '未選擇日期',
          labelCalendar: '日曆',
          labelNav: '日曆導航',
          labelHelp: '請選擇日期',
        },
      },
    };
  },
  methods: {
    onChange: function (event) {
      this.$emit('onChange', {
        inputName: this.inputName,
        value: event,
      });
    },
  },
  mounted: function () {},
};
</script>

