<template>
  <line-content :title="title">
    <many-class-meta
      :input-name="inputName"
      :many-class-metas="manyClassTags"
      :class-meta-tree="classTagTree"
      :multiple="inputMultiple"
      v-on:onChange="onChange"
    />
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/list-control-center/search-box/base/line-content.vue';
import ManyClassTag from '@fanswoo/core/vue/many-class-meta/many-class-meta.vue';

export default {
  props: [
    'inputMultiple',
    'inputName',
    'title',
    'value',
    'classTagTree',
    'manyClassTags',
  ],
  components: {
    LineContent,
    ManyClassTag,
  },
  data: () => {
    return {};
  },
  methods: {
    onChange: function (payload) {
      this.$emit('onChange', {
        inputName: payload.inputName,
        value: payload.value,
      });
    },
  },
  mounted: function () {},
};
</script>
