<template>
  <line-content :title="title">
    <input
      type="number"
      :name="inputName"
      :value="value"
      v-on:change="onChange"
      :placeholder="inputPlaceholder || '請填寫' + title"
      :min="inputMin || 0"
      :max="inputMax || 99999999"
      autocomplete="off"
    />
  </line-content>
</template>

<script setup lang="ts">
import LineContent from '@fanswoo/control-center/custom-component/list-control-center/search-box/base/line-content.vue';
import { onMounted } from "vue";

const props = defineProps({
  inputPlaceholder: {
    type: String,
    default: '',
  },
  inputName: {
    type: String,
    default: '',
  },
  title: {
    type: String,
    default: '',
  },
  value: {
    type: String,
    default: '',
  },
  inputMin: {
    type: Number,
    default: 0,
  },
  inputMax: {
    type: Number,
    default: 99999999,
  },
});

const onChange = (event: Event) => {
  emit('onChange', {
    inputName: inputName,
    value: (event.target as HTMLInputElement).value,
  });
};

onMounted(() => {
  // console.log(props);
});
</script>

