<template>
  <line-content :title="title">
    <select class="search-select" :name="inputName" @change="onChange">
      <template v-for="(option, optionKey) in inputOptions" :key="optionKey">
        <option
          :value="option.value"
          :selected="value == option.value"
          :disabled="option.disabled ? true : false"
          :style="option.style"
        >
          {{ option.text }}
        </option>
      </template>
    </select>
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/list-control-center/search-box/base/line-content.vue';

export default {
  props: ['title', 'value', 'inputName', 'inputOptions'],
  data: () => {
    return {};
  },
  components: {
    LineContent,
  },
  methods: {
    onChange: function (event) {
      this.$emit('onChange', {
        inputName: this.inputName,
        value: event.target.value,
      });
    },
  },
};
</script>
