<template>
  <line-content :title="title">
    <quick-search
      :input-name="inputName"
      :placeholder="inputPlaceholder || '請填寫' + title"
      :url="url"
      :input-value="value"
      :input-width="inputWidth"
      :input-maxlength="inputMaxlength"
      v-on:change="onChange"
    ></quick-search>
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/list-control-center/search-box/base/line-content.vue';
import QuickSearch from '@fanswoo/core/vue/quick-search/quick-search.vue';

export default {
  props: [
    'inputMaxlength',
    'inputName',
    'title',
    'value',
    'inputWidth',
    'url',
    'inputPlaceholder',
  ],
  components: {
    LineContent,
    QuickSearch,
  },
  data: () => {
    return {};
  },
  methods: {
    onChange: function (event) {
      this.$emit('onChange', {
        inputName: this.inputName,
        value: event.target.value,
      });
    },
  },
  mounted: function () {},
};
</script>
