<template>
    <span>
        <input 
            v-for="(value,key) in values"
            type="hidden" 
            :name="inputNameReal" 
            :key="key"
            :value="value"
        >
    </span>
</template>

<script>
export default {
    props: [
        'inputName',
        'value',
    ],
    data: () => {
        return {
            values: [],
            inputNameReal: ''
        }
    },
    mounted: function() {
        if( Array.isArray(this.value) )
        {
            this.values = this.value;
            this.inputNameReal = this.inputName + '[]';
        }
        else
        {
            this.values.push(this.value);
            this.inputNameReal = this.inputName;
        }
    }
};
</script>