<template>
  <line-content
    :title="title"
    :styleClass="styleClass"
    :explanation="explanation"
    :alert="alert"
  >
    <template v-for="(checkbox, key3) in inputCheckboxs" :key="key3">
      <label
        class="checkbox"
        data-toggle="tooltip"
        :title="checkbox.tooltip"
      >
        <input
          type="checkbox"
          :id="inputName + key3"
          :name="inputName + '[]'"
          :value="checkbox.value"
          :checked="
            (value &&
              Array.isArray(value) &&
              value
                .map((v) => v.toString())
                .includes(checkbox.value.toString())) ||
            checkbox.checked
          "
          :disabled="disabled == true || checkbox.disabled"
        />
        {{ checkbox.text }}
      </label>
    </template>
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/setting-control-center/line-content/base/line-content.vue';

export default {
  props: [
    'title',
    'styleClass',
    'value',
    'inputName',
    'inputCheckboxs',
    'inputWidth',
    'inputMaxlength',
    'afterInputText',
    'explanation',
    'disabled',
    'alert',
  ],
  components: {
    LineContent,
  },
  data: () => {
    return {};
  },
  mounted: function () {},
};
</script>
