<template>
  <line-content
    :title="title"
    :styleClass="styleClass"
    :explanation="explanation"
    :alert="alert"
  >
    <input
      type="number"
      :name="inputName"
      :placeholder="inputPlaceholder"
      v-model="filter"
      :style="inputWidth ? { width: inputWidth + 'px' } : null"
      :maxlength="inputMaxlength ? inputMaxlength : null"
      :max="inputMax ? inputMax : null"
      :min="inputMin ? inputMin : null"
      :disabled="disabled ? disabled : null"
      :readonly="readonly ? readonly : null"
    />
    {{ afterInputText }}
  </line-content>
</template>

<script>
import LineContent from '@fanswoo/control-center/custom-component/setting-control-center/line-content/base/line-content.vue';

export default {
  props: [
    'title',
    'styleClass',
    'value',
    'inputName',
    'inputPlaceholder',
    'inputWidth',
    'inputMaxlength',
    'readonly',
    'disabled',
    'inputMax',
    'inputMin',
    'afterInputText',
    'explanation',
    'alert',
  ],
  components: {
    LineContent,
  },
  computed: {
    filter: {
      get() {
        return this.value;
      },
      set(value) {
        if (!value || typeof value === 'string') {
          return;
        } else if (value >= this.inputMin && value <= this.inputMax) {
          this.value = value;
        }
      },
    },
  },
  data: () => {
    return {};
  },
};
</script>
