<template>
  <div @click="onClick" :style="style">
    <RouterLink
      v-if="linkTo"
      :to="{
        path: linkTo,
        query: withSearchSettingFields ? searchSettingFieldValues : null,
      }"
      :name="name"
      :class="['btn', 'router-link-active', buttonStyle]"
      :target="targetBlank ? '_blank' : null"
    >
      <FontAwesomeIcon
        v-if="fontAwesomeIcon"
        class="font-awesome-icon"
        :icon="fontAwesomeIcon"
      />
    </RouterLink>
    <span
      v-else
      :name="name"
      :class="['btn', 'router-link-active', buttonStyle]"
    >
      <FontAwesomeIcon
        v-if="fontAwesomeIcon"
        class="font-awesome-icon"
        :icon="fontAwesomeIcon"
      />
    </span>
  </div>
</template>

<script>
export default {
  props: {
    name: {
      type: String,
      defalut: '',
    },
    linkTo: {
      type: String,
      defalut: '',
    },
    buttonStyle: {
      type: String,
      defalut: '',
    },
    targetBlank: {
      type: Boolean,
      defalut: false,
    },
    fontAwesomeIcon: {
      type: String,
      defalut: '',
    },
    withSearchSettingFields: {
      type: Boolean,
      defalut: false,
    },
    searchSettingFieldValues: {
      type: Object,
      default: function () {
        return {};
      },
    },
    color: {
      type: String,
      default: '#FFF',
    },
    backgroundColor: {
      type: String,
      default: '#AAA',
    },
  },
  computed: {
    style() {
      return {
        '--color': this.color,
        '--background-color': this.backgroundColor,
      };
    },
  },
  methods: {
    onClick(event) {
      this.$emit('onClick', event);
    },
  },
};
</script>

<style scoped lang="scss">
.router-link-active {
  color: var(--color);
  background-color: var(--background-color);
  &:focus {
    color: var(--color);
  }
}
</style>
