  <div class="user-status">
    <!-- <div class="headPortrait" @click="linkToUserCenter()">
            <img :src="user.picUrl">
        </div> -->
    <b-avatar @click="linkToUserCenter()" :src="user.picUrl" />
    <div class="data-box">
