<template>
  <div class="line-content">
    <div class="title">回覆內容</div>
    <div class="field">
      <div
        v-for="InquiryComment in InquiryComments"
        :key="InquiryComment.id"
        class="comment"
      >
        <span class="created-at">{{ InquiryComment.createdAt }}</span>
        <h6>
          {{ InquiryComment.userName }} ( {{ InquiryComment.User.email }} )
        </h6>
        <p v-html="nl2br(InquiryComment.content)"></p>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  props: ['InquiryComments'],
  methods: {
    nl2br(str, isXhtml) {
      if (typeof str === 'undefined' || str === null) {
        return '';
      }
      const breakTag =
        isXhtml || typeof isXhtml === 'undefined' ? '<br />' : '<br>';
      const replace = `$1${breakTag}$2`;
      return str.replace(/([^>\r\n]?)(\r\n|\n\r|\r|\n)/g, replace);
    },
  },
};
</script>
