<template>
  <div class="line-content">
    <div class="title">回覆內容</div>
    <div class="field">
      <div
        class="comment"
        v-for="OrderComment in OrderComments"
        :key="OrderComment.id"
      >
        <span class="created-at">{{ OrderComment.createdAt }}</span>
        <h6>{{ OrderComment.userName }} ( {{ OrderComment.User.email }} )</h6>
        <p v-html="nl2br(OrderComment.content)"></p>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  props: ['OrderComments'],
  data: () => {
    return {};
  },
  methods: {
    nl2br(str, is_xhtml) {
      if (typeof str === 'undefined' || str === null) {
        return '';
      }
      var breakTag =
        is_xhtml || typeof is_xhtml === 'undefined' ? '<br />' : '<br>';
      return (str + '').replace(
        /([^>\r\n]?)(\r\n|\n\r|\r|\n)/g,
        '$1' + breakTag + '$2',
      );
    },
  },
  // mounted() {
  //   console.log(this.OrderComments);
  // }
};
</script>
