import Specification from '@fanswoo/control-center/pages/admin/custom-component/edit-control-center/line-content/shop/product/product/classes/specification';
import SpecificationName from '@fanswoo/control-center/pages/admin/custom-component/edit-control-center/line-content/shop/product/product/classes/specification-name';
import SpecificationColor from '@fanswoo/control-center/pages/admin/custom-component/edit-control-center/line-content/shop/product/product/classes/specification-color';
import { assign } from '@fanswoo/core/utils/util';

test('initialize a specificationName object', () => {
  const specificationName = new SpecificationName();
  expect(specificationName).toBeInstanceOf(SpecificationName);
});

test('initialize a specificationColor object', () => {
  const specificationColor = new SpecificationColor();
  expect(specificationColor).toBeInstanceOf(SpecificationColor);
});

test('initialize a specification object', () => {
  const specification = new Specification();
  expect(specification).toBeInstanceOf(Specification);
});

test('assign a specificationName object', () => {
  const specificationName = new SpecificationName();
  const name = 'L';

  assign(specificationName, {
    name,
  });

  expect(specificationName.name).toBe(name);
});

test('assign a specificationColor object', () => {
  const specificationColor = new SpecificationColor();
  const colorName = 'red';
  const colorRgb = '#FF0000';

  assign(specificationColor, {
    colorName,
    colorRgb,
  });

  expect(specificationColor.colorName).toBe(colorName);
  expect(specificationColor.colorRgb).toBe(colorRgb);
});

test('assign a specification object', () => {
  const specification = new Specification();

  const productId = 15;
  specification.productId = productId;

  const name = 'XL';
  const colorName = 'red';
  const colorRgb = '#FF0000';
  const cost = 50;
  const price = 100;
  const itemCode = 'itemCode';
  const stocknum = 30;
  const prioritynum = 9;

  assign(specification, {
    name,
    colorName,
    colorRgb,
    cost,
    price,
    itemCode,
    stocknum,
    prioritynum,
  });

  expect(specification.productId).toBe(productId);
  expect(specification.name).toBe(name);
  expect(specification.colorName).toBe(colorName);
  expect(specification.colorRgb).toBe(colorRgb);
  expect(specification.cost).toBe(cost);
  expect(specification.price).toBe(price);
  expect(specification.itemCode).toBe(itemCode);
  expect(specification.stocknum).toBe(stocknum);
  expect(specification.prioritynum).toBe(prioritynum);
});

