<template>
  <div class="line-content">
    <div class="title">型別</div>
    <div class="field">
      <SpecificationColor
        ref="specificationColor"
        :specificationColors="specificationColors"
        @alterSpecificationColor="alterSpecificationColor"
      />
      <div class="explanation">
        若有多種型別請填寫型別名稱，例如不同顏色或不同外觀，若沒有多種型別請忽略此選項
      </div>
    </div>
  </div>
</template>

<script setup lang="ts">
import { ref } from 'vue';
import { useStore } from '@/control-center/store/control-center-store';
import Color from '@fanswoo/control-center/pages/admin/custom-component/edit-control-center/line-content/shop/product/product/specification-color-component.vue';

const props = defineProps({
  specificationColors: {
    type: Array,
    default: () => [],
  }
});

const store = useStore();

const alterSpecificationColor = (payload: {
  index: number;
  field: string;
  value: any;
}) => {
  store.commit('ProductSpecification/setSpecificationColor', payload);
};

defineExpose({
  alterSpecificationColor,
});

</script>

