// import Name from '@fanswoo/control-center/pages/admin/custom-component/edit-control-center/line-content/shop/product/product/classes/specification-name';
import Name from '@fanswoo/control-center/pages/admin/custom-component/edit-control-center/line-content/shop/product/product/specification-name-component.vue';

