import { Middleware } from '@fanswoo/controller/main';
import 'bootstrap';
import { Embed } from '@fanswoo/embed/main';
import { AjaxSubmit } from '@fanswoo/ajax-submit/main';

export default class WebMiddleware extends Middleware {
  handle(): void {
    const ajaxSubmit = new AjaxSubmit();
    ajaxSubmit.bindEvent();

    const embed = new Embed();
    embed.parse('body');
    embed.parseHtmlSourceCode('body');

    // Vue.component('Oembed', OEmbed);
    // Vue.component('HtmlSourceCode', HtmlSourceCode);
  }
}
