<template>
  <div
    v-if="
      specificationColors &&
      specificationColors.length > 0 &&
      specificationColors[0].colorName
    "
    v-cloak
    class="color-picker"
  >
    <template v-for="(specificationColor, key) in specificationColors" :key="key">
      <div
        class="color-btn"
        :class="{ clicked: key == colorKey }"
        @click="changeColorKey(key)"
      >
        <div
          class="color"
          :style="{ background: specificationColor.colorRgb }"
        />
      </div>
    </template>
  </div>
</template>

<script>
export default {
  props: {
    specificationColors: {
      type: Array,
      default() {
        return [];
      },
    },
    colorKey: {
      type: Number,
      default: 0,
    },
  },
  data() {
    return {};
  },
  methods: {
    changeColorKey(key) {
      this.$emit('change-color-key', {
        key,
      });
    },
  },
};
</script>

<style lang="scss" scoped>
.color-picker {
  overflow: hidden;
  margin-bottom: 20px;
  .color-btn {
    display: block;
    float: left;
    margin: 0 10px 0 0px;
    border: 1px solid #ccc;
    width: 40px;
    height: 40px;
    font-family: 'Futura T OT';
    cursor: pointer;
    transition: border 0.5s;
    padding: 5px;
    &:hover,
    &.clicked {
      border: 1px solid #000;
    }
    .color {
      height: 100%;
      width: 100%;
      border: 1px solid #000;
    }
  }
}
</style>
