<template>
  <div
    v-if="specificationNames.length > 0 && specificationNames[0].name"
    v-cloak
    class="specification-picker"
  >
    <template v-for="(specificationName, key) in specificationNames" :key="key">
      <span
        :class="{
          clicked: key == nameKey,
        }"
        class="specification-btn"
        @click="changeNameKey(key)"
        v-text="specificationName.name"
      />
    </template>
  </div>
</template>

<script>
export default {
  props: ['specificationNames', 'nameKey'],
  data() {
    return {};
  },
  methods: {
    changeNameKey(key) {
      this.$emit('change-name-key', {
        key,
      });
    },
  },
};
</script>

<style lang="scss" scoped>
.specification-picker {
  overflow: hidden;
  margin-bottom: 40px;
  .specification-btn {
    float: left;
    display: block;
    cursor: pointer;
    transition: 0.5s;
    margin-right: 10px;
    padding: 10px;
    background: #999;
    color: #fff;
    font-size: 14px;
    line-height: 14px;
    &:hover,
    &.clicked {
      background: #000;
    }
  }
}
</style>
